mapper.select:
select count(*) from picture  inner join globalization g on g.tablename='picture' and g.tablecolumn='name' and g.language=#{lang} and g.tableid=picture.id

	 categoryid=${condition.categoryid} 
	 and  g.name like '${condition.name}%'

select g.id as gid,picture.id,g.name,gcomment.name as comment,gcategory.name as category_name,gcomment.id as gcommentid, active as active_v,sort,categoryid,(select count(*) from picture_path where pictureid=picture.id) as imgcount,date_format(g.createtime,'%Y-%m-%d %H:%i:%s') as createtime,(select max(name) from account where id=g.createid) as createname, (select max(name ) from v_globalization_dictionary where tablename='dictionary' and mkey='ACTIVETYPE' and val=picture.active and language=#{lang}) as active from picture inner join globalization g on g.tablename='picture' and g.tablecolumn='name' and g.language=#{lang} and g.tableid=picture.id inner join globalization gcomment on gcomment.tablename='picture' and gcomment.tablecolumn='comment' and gcomment.language=#{lang} and gcomment.tableid=picture.id inner join globalization gcategory on gcategory.tablename='category' and gcategory.tablecolumn='name' and gcategory.language=#{lang} and gcategory.tableid=picture.categoryid
categoryid=${condition.categoryid} and g.name like '${condition.name}%'
order by sort limit ${page.startR},${page.endR}

select count(*) from picture inner join globalization g on g.tablename='picture' and g.language=#{lang}
and g.tablecolumn='name' and g.tableid=picture.id 

	 name=#{name}
